type.raw:
(props, propName) => {
    Children.forEach(props[propName], child => {
        if (
            !(
                child &&
                typeof child.type === 'function' &&
                child.type._typeMark === 'breadcrumb_item'
            )
        ) {
            throw new Error(
                "Breadcrumb's children must be Breadcrumb.Item!"
            );
        }
    });
}
